import React from 'react';

class Item extends React.Component {
    constructor(props) {
        super(props)
        this.state = {name: '', category: '', editMode: false}
    }

    onDelete = () => {
        this.props.onDelete(this.props.index)
    } 

    handleChange = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    toggleEditMode = () => {
        this.setState({name: '', category: '', editMode: !this.state.editMode})
    }

    sendData = () => {
        var data = {
            name: this.state.name,
            category: this.state.category
        }
        this.props.onUpdate(data, this.props.index)
    }

    render() {
        if (this.state.editMode)
            return (
                <form onSubmit={this.sendData} >
                    <div className="row">
                        <input type="text" name="name" className="col-5" onChange={this.handleChange}/>
                        <input type="text" name="category" className="col-5" onChange={this.handleChange}/>
                        <button className="btn btn-primary col-1" onClick={this.sendData}>Update</button>
                        <button className="btn btn-primary col-1">Cancel</button>
                    </div>
                </form>
            )
        return (
            <div className="row">
                <div className="col-5">{this.props.item['name']}</div>
                <div className="col-5">{this.props.item['category']}</div>
                <button className="btn btn-primary col-1" onClick={this.toggleEditMode}>Edit</button>
                <button className="btn btn-primary col-1" onClick={this.onDelete}>Delete</button>
            </div>
        );
    }
}
  
export default Item;
