import React from 'react';

class ItemForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {name: '', category:''}
    }

    handleChange = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    sendData = () => {
        var data = {
            name: this.state.name,
            category: this.state.category
        }
        this.props.onCreate(data)
    }


    render() {
        return (
            <form onSubmit={this.sendData} >
                <div className="row">
                    <input type="text" name="name" className="col-5" />
                    <input type="text" name="category" className="col-5" />
                    <button className="btn btn-primary col-1" onClick={this.sendData}>Add</button>
                </div>
            </form>
        );
    }
}
  
export default ItemForm;
