import React from 'react';
import axios from 'axios';
import Item from './Item';

class ItemDashboard extends React.Component {
    constructor(props) {
        super(props)
        this.state = {items: []}
    }

    componentDidMount() {
        this.triggerUpdate()
    }

    onItemCreation(data) {
        axios.post('http://127.0.0.1:5000/api/', data)
            .then( res => this.setState({items: res.data}))
            .then(this.triggerUpdate)
    }

    onItemUpdate = (data, index) => {
        axios.put('http://127.0.0.1:5000/api/' + index, data)
            .then( res => this.setState({items: res.data}))
            .then(this.triggerUpdate)
    }

    onItemDeletion = (index) => {
        currentComponent = this;
        axios.delete('http://127.0.0.1:5000/api/' + index)
            .then( res => currentComponent.setState({items: res.data}))
            .then(currentComponent.triggerUpdate)
    }

    triggerUpdate() {
        axios.get('http://127.0.0.1:5000/api/')
            .then( res => this.setState({items: res.data}))
    }

    render() {
      const itemList = []
      for (var item in this.state.items)
        itemList.push(<Item item={this.state.items[item]} key={item} index={item} onDelete={this.onItemDeletion} />)
        return (
            <div className="container">
                {itemList}
            </div>

        );
    }
}

export default ItemDashboard;
