import React from 'react';
import axios from 'axios';

class ItemDashboard extends React.Component {
    constructor(props) {
        super(props)
        this.state = {items: []}
    }

    componentDidMount() {
        this.triggerUpdate()
    }

    triggerUpdate() {
        axios.get('http://127.0.0.1:5000/api/')
            .then( res => res.json())
            .then( res => {
                this.state.items = []
                for (item in res)
                    this.state.items.push(<Item item={res[item]} />)
            })
    }

    render() {
        return (
            <div class="container">
                Sup?
            </div>
        );
    }
}

export default ItemDashboard;
