import React from 'react';

class Item extends React.Component {
    constructor(props) {
        super(props)
        this.state = {name: '', category: '', editMode: false}
    }

    onDelete = () => {
        this.props.onDelete(this.props.index)
    } 

    handleChange = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    toggleEditMode = () => {
        this.setState({editMode: !this.state.editMode})
    }

    sendData = () => {
        var data = {
            name: this.state.name,
            category: this.state.category
        }
        this.props.onUpdate(data, this.props.index)
    }

    render() {
        return (
            <div className="row">
                <div className="col-5">{this.props.item['name']}</div>
                <div className="col-5">{this.props.item['category']}</div>
                <button className="btn btn-primary col-1" onClick={this.toggleEditMode}>Edit</button>
                <button className="btn btn-primary col-1" onClick={this.onDelete}>Delete</button>
            </div>
        );
    }
}
  
export default Item;
